function animateCounter(t){let e=parseInt(t.getAttribute("data-target")),n=0,r=e/100,a=setInterval(()=>{n+=r,t.textContent=Math.floor(n).toLocaleString("pt-BR"),n>=e&&(t.textContent="+"+e.toLocaleString("pt-BR"),clearInterval(a))},10)}function setupCounterObserver(){let t=document.querySelectorAll(".numbers"),e=new IntersectionObserver(t=>{t.forEach(t=>{t.isIntersecting&&!t.target.classList.contains("animated")&&(t.target.classList.add("animated"),animateCounter(t.target),e.unobserve(t.target))})},{threshold:.7});t.forEach(t=>{e.observe(t)})}document.addEventListener("DOMContentLoaded",setupCounterObserver);